    public class TestMessageIdsGenerator : IMessageIdGenerator
    {
        private static readonly ulong[] MessageIds = { 0x51e57ac42770964aUL, 0x51e57ac917717a27UL };
        private int _mi;

        public ulong GetNextMessageId()
        {
            return MessageIds[_mi++];
        }
    }

    public class TestNonceGenerator : INonceGenerator, IDisposable
    {
        private static readonly byte[] Nonces = ("3E0549828CCA27E966B301A48FECE2FC" + "311C85DB234AA2640AFC4A76A735CF5B1F0FD68BD17FA181E1229AD867CC024D").HexToBytes();
        private TLStreamer _nonceStream = new TLStreamer(Nonces);

        public byte[] GetNonce(uint length)
        {
            return _nonceStream.ReadBytes((int)length);
        }

        public void Dispose()
        {
            if (_nonceStream != null)
            {
                _nonceStream.Dispose();
                _nonceStream = null;
            }
        }
    }
